import {Button, TextField, Typography} from '@material-ui/core';
import React, {Component} from "react";
import FirebaseService from "../../services/FirebaseService";
import {urls} from "../../util/urlUtils";
import {withRouter} from "react-router-dom";

class Add extends Component{
    submit = (event) => {
        event.preventDefault();

        const {nome} = this;
        const {dob} = this;
        const {status} = this;

        const newId = FirebaseService.pushData('pets',{
            nome,
            dob,
            status
        });
        this.props.history.push(urls.data.path);
    };

    render = () => {
        return <React.Fragment>
            <Typography variant="headline" component="h2"> Add </Typography>
            <form onSubmit={this.submit}>
                <TextField className="input-field"
                           type="text" 
                           defaultValue={''}
                           label="Nome"
                           required
                           onChange={e => this.nome = e.target.value}/>

                <TextField className="input-field"
                           type="text" 
                           defaultValue={''}
                           label="Nascimento"
                           required
                           onChange={e => this.dob = e.target.value}/>

                <TextField className="input-field"
                           type="text" 
                           defaultValue={''}
                           label="Status"
                           required
                           onChange={e => this.status = e.target.value}/>
                <Button type="submit" style={{marginTop: '20px', display: 'inline-block'}}> Add </Button>
            </form>
        </React.Fragment>
    }
};

export default withRouter(Add);
